"use strict";function getList(){$.ajax({url:"../lib/nav_top.json",dataType:"json",success:function(o){var t="";o.forEach(function(n){t+="<li>".concat(n.name,"</li>")}),$(".nav_top > ul").html(t).on({mouseenter:function(){return $(".nav_box").stop().slideDown()},mouseleave:function(){return $(".nav_box").stop().slideUp()}}).children("li").on("mouseover",function(){var n=$(this).index(),t=o[n].list,i="";t.forEach(function(n){i+='\n              <li>\n                <div>\n                  <img src="'.concat(n.list_url,'" alt="">\n                </div>\n                <p class="title">').concat(n.list_name,'</p>\n                <span class="price">').concat(n.list_price,"</span>\n              </li>\n            ")}),$(".nav_box > ul").html(i)}),$(".nav_top li").on({mouseover:function(){$(this).css("color","orangered")},mouseout:function(){$(this).css("color","#333")}}),$(".nav_box").on({mouseover:function(){$(this).finish().show()},mouseout:function(){$(this).finish().slideUp()}})}})}function getList2(){$.ajax({url:"../lib/nav_list.json",dataType:"json",success:function(n){var t="";n.forEach(function(n){t+="<li>".concat(n.name,"</li>")}),$(".nav_list > ul").html(t)}})}getList(),getList2();var mySwiper=new Swiper(".swiper-container",{loop:!0,autoplay:{delay:2e3},effect:"cube",pagination:{el:".swiper-pagination",clickable:!0},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});