"use strict";var flag=!0,list2=[];function getList(){$.ajax({url:"../lib/list.json",dataType:"json",success:function(i){$(".pagi").pagination({pageCount:Math.ceil(i.length/8),current:1,jump:!0,coping:!0,homePage:"首页",endPage:"末页",prevContent:"上页",nextContent:"下页",callback:function(n){var t=n.getCurrent();bindHtml(i.slice(8*(t-1),8*t))}}),bindHtml(i.slice(0,8)),list2=i}})}function bindHtml(n){var t="";n.forEach(function(n){t+='\n      <li>\n        <img src="'.concat(n.img,'" alt="">\n        <p>').concat(n.name,"</p>\n        <p>").concat(n.id,"</p>\n        <p>").concat(n.price,"</p>\n      </li>\n    ")}),$(".box > ul").html(t)}getList();var btn=document.querySelector(".order");btn.onclick=function(){flag=!flag,list2.sort(function(n,t){return!0===flag?n.id-t.id:t.id-n.id}),$(".pagi").pagination({pageCount:Math.ceil(list2.length/8),current:1,jump:!0,coping:!0,homePage:"首页",endPage:"末页",prevContent:"上页",nextContent:"下页",callback:function(n){var t=n.getCurrent();bindHtml(list2.slice(8*(t-1),8*t))}}),bindHtml(list2.slice(0,8))};