"use strict";var flag=!0,list2=[];function getList(){$.ajax({url:"../lib/list.json",dataType:"json",success:function(i){$(".pagi").pagination({pageCount:Math.ceil(i.length/8),current:1,jump:!0,coping:!0,homePage:"首页",endPage:"末页",prevContent:"上页",nextContent:"下页",callback:function(t){var n=t.getCurrent();bindHtml(i.slice(8*(n-1),8*n))}}),bindHtml(i.slice(0,8)),list2=i}})}function bindHtml(t){var n="";t.forEach(function(t){n+='\n      <li data-id="'.concat(t.id,'">\n        <img src="').concat(t.img,'" alt="">\n        <p>商品名称：').concat(t.name,"</p>\n        <p>商品编号：").concat(t.id,"</p>\n        <p>价格：").concat(t.price," 元</p>\n      </li>\n    ")}),$(".box > ul").html(n)}getList();var btn=document.querySelector(".order");btn.onclick=function(){flag=!flag,list2.sort(function(t,n){return!0===flag?t.id-n.id:n.id-t.id}),$(".pagi").pagination({pageCount:Math.ceil(list2.length/8),current:1,jump:!0,coping:!0,homePage:"首页",endPage:"末页",prevContent:"上页",nextContent:"下页",callback:function(t){var n=t.getCurrent();bindHtml(list2.slice(8*(n-1),8*n))}}),bindHtml(list2.slice(0,8))},$(".box > ul").on("click","li",function(){console.log(this);for(var t=$(this).data("id"),n=null,i=0;i<list2.length;i++)if(list2[i].id===t){n=list2[i];break}localStorage.setItem("goodsInfo",JSON.stringify(n)),window.location.href="./detail.html"});